import api from '../constants/api';
import { PORTFOLIO, ASSET, TRANSACTION, LOG_DATA } from './constants/endpoints';
import { userToken } from '../constants/auth';

export const fetchPortfolio = createAsyncThunk(
	'portfolio/fetchPortfolio',
	async (arg, { rejectWithValue }) => {
		try {
			const config = {
				headers: {
					Authorization: `Bearer ${userToken}`,
				},
			};

			const response = await api.get(`/api/portfolio/`, config);
			console.log(response.data);
			return response.data;
		} catch (error) {
			if (error.response && error.response.data.message) {
				console.log(error.message);
				return rejectWithValue(error.response.data.message);
			} else {
				console.log('source: fetchPortfolio:' + error.message);
				return rejectWithValue(error.message);
			}
		}
	}
);

const initialState = {
	id: 0,
	balance: 0,
	isLoading: false,
	error: '',
};

const portfolioSlice = createSlice({
	name: 'portfolio',
	initialState,
	reducers: {},
	extraReducers: {
		[fetchPortfolio.pending]: state => {
			console.log('fetch portfolio pending');
			state.isLoading = true;
		},
		[fetchPortfolio.fulfilled]: (state, { payload }) => {
			console.log('fetch portfolio fulfilled');
			state.isLoading = false;
			state.balance = payload.balance;
			state.id = payload.id;
		},
		[fetchPortfolio.rejected]: (state, { payload }) => {
			console.log('fetch portfolio rejected');
			state.isLoading = false;
			state.error = payload;
		},
	},
});

export const {} = portfolioSlice.actions;
export default portfolioSlice.reducer;
